mapper.select:
select
id, project_id, requirement_id, stage_id,stage_type, name, assignment_desc, assignment_status, update_time,
create_time
from waterfall.assignment


        and id = #{id}


        and project_id = #{projectId}


        and requirement_id = #{requirementId}


        and stage_id = #{stageId}


        and stage_type = #{stageType}


        and name = #{name}


        and assignment_desc = #{assignmentDesc}


        and assignment_status = #{assignmentStatus}


        and update_time = #{updateTime}


        and create_time = #{createTime}
