mapper.select:
select
id, assignment_id, stage_id, requirement_id, project_id, worker, work_status, start_time, complete_time,
update_time, create_time
from waterfall.assignment_worker


        and id = #{id}


        and assignment_id = #{assignmentId}


        and stage_id = #{stageId}


        and requirement_id = #{requirementId}


        and project_id = #{projectId}


        and worker = #{worker}


        and work_status = #{workStatus}


        and start_time = #{startTime}


        and complete_time = #{completeTime}
