mapper.select:
select id, project_id, template_id, name, requirement_desc, expect_date, priority, requirement_status, requirement_owner, update_time, create_time from waterfall.requirement where id in
#{item}
and requirement_status = #{status}

select
id, project_id, template_id, name, requirement_desc, expect_date,priority,requirement_status,requirement_owner,
update_time,
create_time
from waterfall.requirement


        and id = #{id}


        and project_id = #{projectId}


        and template_id = #{templateId}


        and name = #{name}


        and requirement_desc = #{requirementDesc}


        and expect_date = #{expectDate}


        and priority = #{priority}


        and requirement_status = #{requirementStatus}


        and requirement_owner = #{requirementOwner}


        and update_time = #{updateTime}


        and create_time = #{createTime}
