mapper.select:
select
  id, project_id, template_id, name, requirement_desc, requirement_owner, update_time, create_time
from waterfall.requirement


        and id = #{id}


        and project_id = #{projectId}


        and template_id = #{templateId}


        and name = #{name}


        and requirement_desc = #{requirementDesc}


        and requirement_owner = #{requirementOwner}


        and update_time = #{updateTime}


        and create_time = #{createTime}
